package singletondemo;/** * @author lhj * @date 2023/2/23 * @apiNote * 双重检查锁方式 */public class LanSingleton {    private LanSingleton() {    }    private static volatile LanSingleton instance;    public static LanSingleton getInstance() {        if (instance == null) {            synchronized (LanSingleton.class) {                if (instance == null) {                    instance = new LanSingleton();                }            }        }        return instance;    }}